import React, {useState} from 'react';
import { Link } from 'react-router-dom';

  

function CadastroLogin(){
    const [dadosLogin, setDadosLogin] = useState({
        nomeLogin: '',
        emailLogin: '',
        telefone: ''
    })
    
    function handleInputChange(e){
        dados[e.target.name] = e.target.value;
        setDados(dados);
    }
    
    function handleSubmit(e) {
        e.preventDefault();    
        console.log("Você clicou em Cadastrar.");
    }

    return(
        <div>
            <h1>
                Cadastre-se no nosso Sistema
            </h1>
            <form>
                <div>
                    <label>Nome Completo: </label>
                    <input type="text" name="nomeCompleto"></input>
                </div>
                <div>
                    <label>E-mail: </label>
                    <input type="text" name="emailUsuario"></input>
                </div>
                <div>
                    <label>Senha: </label>
                    <input type="password" name="senhaUsuario"></input>
                </div>
                <div>
                    <label>Confirme sua senha: </label>
                    <input type="password" name="confirmacaoSenha"></input>
                </div>
                <button type="submit" name="botaoCadastrar">Cadastrar</button>
            </form>
        </div>
    )
}

export default CadastroLogin;

