import React, {useState} from 'react';
import Axios from 'axios';
  

function CadastroLogin(){
    const [dadosLogin, setDadosLogin] = useState({
        nomeLogin: '',
        emailLogin: '',
        senhaLogin: '',
        confirmarSenha: ''
    })

    const [status, setStatus] = useState({
      type:'',
      mensagem: ''
    })
    
    function handleInputChange(e){
        dadosLogin[e.target.name] = e.target.value;
        setDadosLogin(dadosLogin);
    }

    // Função para comprar Senha e Confirmação
    function confirmacaoSenha(){
        const senha = dadosLogin.senhaLogin;
        const confSenha = dadosLogin.confirmarSenha;
        if(senha !== confSenha){
            alert("As senhas são diferentes");
        }
    }

    function validarNome(){
        const campoNome = dadosLogin.nomeLogin.length;
        console.log(campoNome);
    }
       
    function handleSubmit(e) {
        e.preventDefault();    
        console.log("Você clicou em Cadastrar.");
        confirmacaoSenha(dadosLogin);

        ////////////////////////////////////////////////////

        const dadosSalvos = true;
        if(dadosSalvos){
          setStatus({
            type: 'success',
            mensagem: 'Usuário Cadastrado com sucesso!'
          });
          setDadosLogin({
            nomeLogin: '',
            emailLogin: '',
            senhaLogin: '',
            confirmarSenha: ''
          });
          
        } else {
          setStatus({
            type: 'error',
            mensagem: 'Usuário Não Foi Cadastrado!'
          })
        }

        ////////////////////////////////////////////////

        Axios.post("http://localhost:3080/registrar", {
          nomeLogin: dadosLogin.nomeLogin,
          emailLogin: dadosLogin.emailLogin,
          senhaLogin: dadosLogin.senhaLogin
        }).then((response) => {
          console.log(response);
        })
    }

    return(
        <div>
          <h1>
            Cadastre-se no nosso Sistema
          </h1>

          {/*  */}
          <div>
            {status.type === 'success' ? <p style={{color: "green"}}>{status.mensagem}</p> : " "}
            {status.type === 'error' ? <p style={{color: "red"}}>{status.mensagem}</p> : " "}
          </div>
          {/*  */}

            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nome Completo: </label>
                    <input type="text" name="nomeLogin" id="nomeLogin" maxLength="50" 
                    onChange={handleInputChange} requier>
                    </input>
                </div>
                <div>
                    <label>E-mail: </label>
                    <input type="email" name="emailLogin" maxLength="50" onChange={handleInputChange} onBlur={validarNome(dadosLogin)}></input>
                </div>
                <div>
                    <label>Senha: </label>
                    <input type="password" name="senhaLogin" maxLength="10" onChange={handleInputChange}></input>
                </div>
                <div>
                    <label>Confirme sua senha: </label>
                    <input type="password" name="confirmarSenha" maxLength="10" onChange={handleInputChange}></input>
                </div>
                <button type="submit" name="botaoCadastrar">
                    Cadastrar
                </button>
            </form>
        </div>
    )
}

export default CadastroLogin;

