import React from "react";
import Axios from "axios";
import * as yup from "yup";
import { ErrorMessage, Formik, Form, Field } from "formik";

function CadastroLogin() {
  /*
  const [dadosLogin, setDadosLogin] = useState({
    nomeLogin: "",
    emailLogin: "",
    senhaLogin: "",
    confirmarSenha: "",
