import "./Login.css";
import React from "react";
import { Link } from "react-router-dom";

function Login() {
  return (
    <div className="App">
      <h3>Tela de login</h3>

      <form>
        <div>
          <label>E-mail:</label>
          <input
            type="email"
            name="emailAcesso"
            maxLength="60"
            onChange={handleLogin}
            required
          ></input>
        </div>
      </form>

      <div>
        <Link to="/cadastro-login">Cadastre-se</Link>
      </div>
    </div>
  );
}

export default Login;
