import React, {useState} from 'react';
import { Link } from 'react-router-dom';

  

function CadastroLogin(){
    const [dadosLogin, setDadosLogin] = useState({
        nomeLogin: '',
        emailLogin: '',
        senhaLogin: '',
        confirmacaoSenha: ''
    })
    
    function handleInputChange(e){
        dadosLogin[e.target.name] = e.target.value;
        setDadosLogin(dadosLogin);
        confirmacaoSenha()
    }

    function confirmacaoSenha(){
        if(senhaLogin !== confirmacaoSenha){
            alert("Senhas diferentes")
        }
    }
    
    function handleSubmit(e) {
        e.preventDefault();    
        confirmacaoSenha();
        console.log("Você clicou em Cadastrar.");
    }

    return(
        <div>
            <h1>
                Cadastre-se no nosso Sistema
            </h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nome Completo: </label>
                    <input type="text" name="nomeLogin" onChange={handleInputChange}></input>
                </div>
                <div>
                    <label>E-mail: </label>
                    <input type="email" name="emailLogin" onChange={handleInputChange}></input>
                </div>
                <div>
                    <label>Senha: </label>
                    <input type="password" name="senhaLogin" onChange={handleInputChange}></input>
                </div>
                <div>
                    <label>Confirme sua senha: </label>
                    <input type="password" name="confirmacaoSenha" onChange={handleInputChange}></input>
                </div>
                confirmacaoSenha()
                <button type="submit" name="botaoCadastrar">Cadastrar</button>
            </form>
        </div>
    )
}

export default CadastroLogin;

